import React, { useRef, useState, useEffect } from 'react';
import * as THREE from 'three';
import { Canvas, useFrame, useThree } from '@react-three/fiber';
import { Text, useTexture, Html } from '@react-three/drei';

// Données fictives pour les compétences
const skills = [
  { name: 'React', level: 5, logo: '/api/placeholder/100/100' },
  { name: 'JavaScript', level: 5, logo: '/api/placeholder/100/100' },
  { name: 'HTML/CSS', level: 4, logo: '/api/placeholder/100/100' },
  { name: 'Node.js', level: 4, logo: '/api/placeholder/100/100' },
  { name: 'Three.js', level: 3, logo: '/api/placeholder/100/100' },
  { name: 'TypeScript', level: 4, logo: '/api/placeholder/100/100' },
  { name: 'Git', level: 5, logo: '/api/placeholder/100/100' },
  { name: 'MongoDB', level: 3, logo: '/api/placeholder/100/100' },
];

// Composant pour les étoiles de niveau
const SkillStars = ({ level }) => {
  return (
    <div className="flex">
      {Array.from({ length: 5 }).map((_, i) => (
        <span key={i} className={`text-lg ${i < level ? 'text-yellow-500' : 'text-gray-300'}`}>★</span>
      ))}
    </div>
  );
};

// Composant pour chaque logo de compétence
const SkillLogo = ({ skill, position }) => {
  const meshRef = useRef();
  const [hovered, setHover] = useState(false);
  const texture = useTexture(skill.logo);

  // Animation de perturbation
  useFrame((state) => {
    if (!hovered) {
      meshRef.current.position.x = position[0] + Math.sin(state.clock.elapsedTime * 0.5 + position[1]) * 0.2;
      meshRef.current.position.y = position[1] + Math.cos(state.clock.elapsedTime * 0.3 + position[0]) * 0.2;
      meshRef.current.rotation.z = Math.sin(state.clock.elapsedTime * 0.2) * 0.1;
    } else {
      // Revenir à la position d'origine en cas de hover
      meshRef.current.position.x = position[0];
      meshRef.current.position.y = position[1];
      meshRef.current.rotation.z = 0;
    }
  });

  return (
    <mesh
      ref={meshRef}
      position={[position[0], position[1], 0]}
      onPointerOver={() => setHover(true)}
      onPointerOut={() => setHover(false)}
    >
      <planeGeometry args={[1, 1]} />
      <meshBasicMaterial map={texture} transparent />

      {hovered && (
        <Html center position={[0, -1, 0]}>
          <div className="bg-black bg-opacity-70 p-2 rounded text-white text-center w-32">
            <p className="font-bold">{skill.name}</p>
            <SkillStars level={skill.level} />
          </div>
        </Html>
      )}
    </mesh>
  );
};

// Composant pour la scène des compétences
const SkillsScene = () => {
  const { viewport } = useThree();
  const [positions, setPositions] = useState([]);

  // Calculer des positions dispersées pour les logos
  useEffect(() => {
    const newPositions = [];
    const spacing = Math.min(viewport.width, viewport.height) / 4;

    for (let i = 0; i < skills.length; i++) {
      // Distribution semi-aléatoire mais qui évite les chevauchements
      const x = (Math.random() - 0.5) * viewport.width * 0.8;
      const y = (Math.random() - 0.5) * viewport.height * 0.8;
      newPositions.push([x, y]);
    }

    setPositions(newPositions);
  }, [viewport]);

  return (
    <>
      {skills.map((skill, index) => (
        positions[index] && <SkillLogo key={skill.name} skill={skill} position={positions[index]} />
      ))}
    </>
  );
};

// Composant pour la section Compétences
const Skills = () => {
  return (
    <div className="h-screen w-full">
      <h2 className="text-center text-3xl font-bold mb-8 pt-16">Compétences techniques</h2>
      <div className="h-4/5 w-full">
        <Canvas camera={{ position: [0, 0, 5], fov: 75 }}>
          <ambientLight intensity={0.5} />
          <SkillsScene />
        </Canvas>
      </div>
    </div>
  );
};

// Composant pour la section Hero 3D
const Hero3D = () => {
  return (
    <div className="h-screen w-full bg-gradient-to-br from-blue-900 to-purple-900 flex flex-col justify-center items-center text-white">
      <Canvas className="absolute inset-0" camera={{ position: [0, 0, 5] }}>
        <ambientLight intensity={0.2} />
        <pointLight position={[10, 10, 10]} />
        <HeroMesh />
      </Canvas>
      <div className="z-10 text-center">
        <h1 className="text-5xl font-bold mb-4">Votre Nom</h1>
        <p className="text-2xl">Développeur Web | Créatif | Innovant</p>
      </div>
    </div>
  );
};

// Animation 3D pour la section Hero
const HeroMesh = () => {
  const mesh = useRef();

  useFrame(() => {
    mesh.current.rotation.x = mesh.current.rotation.y += 0.01;
  });

  return (
    <mesh ref={mesh}>
      <torusKnotGeometry args={[1.5, 0.5, 128, 32]} />
      <meshStandardMaterial 
        color="hotpink"
        emissive="purple"
        emissiveIntensity={0.5}
        wireframe
      />
    </mesh>
  );
};

// Composant principal du Portfolio
const Portfolio = () => {
  return (
    <div className="bg-gray-100 min-h-screen">
      <Hero3D />
      <Skills />

      {/* Ajouter d'autres sections ici */}
      <div className="h-screen flex items-center justify-center">
        <h2 className="text-3xl">Autres sections à venir...</h2>
      </div>
    </div>
  );
};

export default Portfolio;
