import React, { useState, useEffect, useRef } from 'react';
import { Camera, Monitor, Code, User, Mail, FileText, Github, Linkedin, Twitter } from 'lucide-react';
import './Portfolio.css'; // Importation du fichier CSS pour les animations

// Composant principal du portfolio
export default function Portfolio() {
  const [scrollPosition, setScrollPosition] = useState(0);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const containerRef = useRef(null);
  const [activeSection, setActiveSection] = useState('home');
  const [menuOpen, setMenuOpen] = useState(false);
  const [hoveredSkill, setHoveredSkill] = useState(null);

  // Données des projets
  const projects = [
    { 
      id: 1, 
      title: "Application E-commerce", 
      description: "Application complète avec panier, paiement et gestion des produits", 
      technologies: ["React", "Node.js", "MongoDB"]
    },
    { 
      id: 2, 
      title: "Dashboard Analytique", 
      description: "Interface analytique avec graphiques en temps réel", 
      technologies: ["React", "D3.js", "Firebase"]
    },
    { 
      id: 3, 
      title: "Système de Gestion de Contenu", 
      description: "CMS personnalisé avec éditeur WYSIWYG", 
      technologies: ["React", "GraphQL", "PostgreSQL"]
    }
  ];

  // Données des compétences/outils avec étoiles d'expérience
  const skills = [
    { name: "React", stars: 5, color: "#61DAFB", icon: "⚛️" },
    { name: "JavaScript", stars: 5, color: "#F7DF1E", icon: "JS" },
    { name: "HTML5", stars: 5, color: "#E34F26", icon: "H" },
    { name: "CSS3", stars: 4, color: "#1572B6", icon: "C" },
    { name: "Node.js", stars: 4, color: "#339933", icon: "N" },
    { name: "TypeScript", stars: 4, color: "#3178C6", icon: "TS" },
    { name: "MongoDB", stars: 3, color: "#47A248", icon: "M" },
    { name: "GraphQL", stars: 3, color: "#E10098", icon: "G" },
    { name: "SASS", stars: 3, color: "#CC6699", icon: "S" },
    { name: "Python", stars: 4, color: "#3776AB", icon: "PY" },
    { name: "Docker", stars: 3, color: "#2496ED", icon: "D" },
    { name: "Git", stars: 5, color: "#F05032", icon: "G" }
  ];

  // Effet pour gérer le défilement et la position de la souris
  useEffect(() => {
    const handleScroll = () => {
      setScrollPosition(window.scrollY);
      const sections = ['home', 'projects', 'skills', 'contact'];

      sections.forEach(section => {
        const element = document.getElementById(section);
        if (element) {
          const rect = element.getBoundingClientRect();
          if (rect.top <= window.innerHeight / 2 && rect.bottom >= window.innerHeight / 2) {
            setActiveSection(section);
          }
        }
      });
    };

    const handleMouseMove = (event) => {
      if (containerRef.current) {
        const rect = containerRef.current.getBoundingClientRect();
        setMousePosition({
          x: ((event.clientX - rect.left) / rect.width) * 2 - 1,
          y: -((event.clientY - rect.top) / rect.height) * 2 + 1
        });
      }
    };

    window.addEventListener('scroll', handleScroll);
    window.addEventListener('mousemove', handleMouseMove);

    return () => {
      window.removeEventListener('scroll', handleScroll);
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  // Calculer la couleur de fond dynamique en fonction du défilement et de la position de la souris
  const bgGradient = `linear-gradient(
    ${135 + mousePosition.x * 30}deg, 
    rgba(${10 + scrollPosition / 20}, ${50 + scrollPosition / 10}, ${120 - scrollPosition / 15}, 0.9),
    rgba(${80 - scrollPosition / 25}, ${20 + scrollPosition / 15}, ${100 + scrollPosition / 20}, 0.8)
  )`;

  // Toggle menu mobile
  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  // Fonction pour générer le cercle de compétences
  const generateSkillsCircle = () => {
    const totalSkills = skills.length;
    const radius = 150; // Rayon du cercle

    return skills.map((skill, index) => {
      // Calculer la position sur le cercle
      const angle = (index / totalSkills) * 2 * Math.PI;
      const x = radius * Math.cos(angle);
      const y = radius * Math.sin(angle);

      // Ajouter un effet de rotation basé sur la position de la souris
      const dynamicX = x + (mousePosition.x * 10);
      const dynamicY = y + (mousePosition.y * 10);

      return (
        <div
          key={skill.name}
          className="absolute transform transition-all duration-300 cursor-pointer"
          style={{
            transform: `translate(${dynamicX}px, ${dynamicY}px) scale(${hoveredSkill === skill.name ? 1.2 : 1})`,
            zIndex: hoveredSkill === skill.name ? 10 : 1
          }}
          onMouseEnter={() => setHoveredSkill(skill.name)}
          onMouseLeave={() => setHoveredSkill(null)}
        >
          <div 
            className={`w-16 h-16 rounded-full flex items-center justify-center text-white font-bold shadow-lg skill-logo ${hoveredSkill === skill.name ? 'animate-pulse' : ''}`}
            style={{ backgroundColor: skill.color }}
          >
            {skill.icon}
          </div>

          {hoveredSkill === skill.name && (
            <div className="absolute top-full left-1/2 transform -translate-x-1/2 mt-2 bg-black bg-opacity-80 backdrop-blur-sm p-2 rounded-lg text-white whitespace-nowrap">
              <p className="font-medium text-center">{skill.name}</p>
              <div className="flex justify-center mt-1">
                {[...Array(5)].map((_, i) => (
                  <span key={i} className="mx-0.5">
                    {i < skill.stars ? "★" : "☆"}
                  </span>
                ))}
              </div>
            </div>
          )}
        </div>
      );
    });
  };

  return (
    <div 
      ref={containerRef}
      className="w-full min-h-screen overflow-x-hidden"
      style={{ background: bgGradient }}
    >
      {/* Navigation */}
      <nav className="fixed top-0 left-0 w-full z-50 bg-black bg-opacity-50 backdrop-blur-sm">
        <div className="container mx-auto px-6 py-4 flex items-center justify-between">
          <div className="text-white font-bold text-2xl transform hover:scale-105 transition-transform duration-300">
            <span className="text-blue-400">Dev</span>Portfolio
          </div>

          {/* Menu mobile */}
          <button 
            className="md:hidden text-white"
            onClick={toggleMenu}
          >
            <div className="w-6 h-1 bg-white mb-1"></div>
            <div className="w-6 h-1 bg-white mb-1"></div>
            <div className="w-6 h-1 bg-white"></div>
          </button>

          {/* Menu desktop */}
          <ul className="hidden md:flex space-x-8">
            {['home', 'projects', 'skills', 'contact'].map((section) => (
              <li key={section}>
                <a 
                  href={`#${section}`}
                  className={`text-lg font-medium transition-all duration-300 hover:text-blue-400 ${
                    activeSection === section ? 'text-blue-400 font-bold' : 'text-white'
                  }`}
                >
                  {section.charAt(0).toUpperCase() + section.slice(1)}
                </a>
              </li>
            ))}
          </ul>
        </div>

        {/* Menu mobile déplié */}
        {menuOpen && (
          <div className="md:hidden bg-black bg-opacity-90 py-4">
            <ul className="flex flex-col items-center">
              {['home', 'projects', 'skills', 'contact'].map((section) => (
                <li key={section} className="py-2">
                  <a 
                    href={`#${section}`}
                    className={`text-lg font-medium transition-all duration-300 hover:text-blue-400 ${
                      activeSection === section ? 'text-blue-400 font-bold' : 'text-white'
                    }`}
                    onClick={() => setMenuOpen(false)}
                  >
                    {section.charAt(0).toUpperCase() + section.slice(1)}
                  </a>
                </li>
              ))}
            </ul>
          </div>
        )}
      </nav>

      {/* Section d'accueil avec effet 3D */}
      <section 
        id="home" 
        className="h-screen flex flex-col justify-center items-center relative overflow-hidden"
        style={{ 
          perspective: '1000px',
          transformStyle: 'preserve-3d'
        }}
      >
        <div 
          className="text-center transform transition-transform duration-500 z-10"
          style={{ 
            transform: `translateZ(20px) rotateX(${mousePosition.y * 5}deg) rotateY(${mousePosition.x * 5}deg)`,
          }}
        >
          <h1 className="text-5xl md:text-7xl font-bold text-white mb-6">
            <span className="block">Développeur</span>
            <span className="text-blue-400 block mt-2">Créatif & Passionné</span>
          </h1>
          <p className="text-xl md:text-2xl text-gray-300 max-w-2xl mx-auto mb-8">
            Je transforme des idées en expériences web interactives et innovantes
          </p>
          <div className="flex flex-col md:flex-row gap-4 justify-center">
            <a 
              href="#projects" 
              className="px-8 py-3 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-lg shadow-lg transform hover:scale-105 transition-all duration-300"
            >
              Voir mes projets
            </a>
            <a 
              href="#contact" 
              className="px-8 py-3 border-2 border-white text-white font-semibold rounded-lg hover:bg-white hover:text-blue-900 transform hover:scale-105 transition-all duration-300"
            >
              Me contacter
            </a>
          </div>
        </div>

        {/* Éléments 3D flottants (déplacés pour ne pas masquer le texte) */}
        <div 
          className="absolute floating-element"
          style={{ 
            top: `${30 + mousePosition.y * 5}%`,
            left: `${5 + mousePosition.x * 5}%`,
            transform: `translateZ(${50 + scrollPosition / 20}px) rotate(${scrollPosition / 20}deg)`,
            transition: 'transform 0.2s ease-out',
            zIndex: 1
          }}
        >
          <div className="w-20 h-20 md:w-32 md:h-32 rounded-lg bg-blue-600 bg-opacity-30 backdrop-blur-md shadow-xl" />
        </div>

        <div 
          className="absolute floating-element"
          style={{ 
            top: `${20 - mousePosition.y * 3}%`,
            right: `${10 - mousePosition.x * 3}%`,
            transform: `translateZ(${30 + scrollPosition / 30}px) rotate(${-scrollPosition / 25}deg)`,
            transition: 'transform 0.2s ease-out',
            zIndex: 1
          }}
        >
          <div className="w-16 h-16 md:w-24 md:h-24 rounded-full bg-purple-500 bg-opacity-20 backdrop-blur-md shadow-xl" />
        </div>

        <div 
          className="absolute floating-element"
          style={{ 
            bottom: `${15 - mousePosition.y * 4}%`,
            left: `${60 + mousePosition.x * 4}%`,
            transform: `translateZ(${40 + scrollPosition / 25}px) rotate(${scrollPosition / 15}deg)`,
            transition: 'transform 0.2s ease-out',
            zIndex: 1
          }}
        >
          <div className="w-14 h-14 md:w-20 md:h-20 bg-teal-400 bg-opacity-25 backdrop-blur-md transform rotate-45 shadow-xl" />
        </div>
      </section>

      {/* Section projets */}
      <section id="projects" className="py-20 px-6">
        <div className="container mx-auto max-w-6xl">
          <h2 className="text-4xl font-bold text-white mb-12 text-center">
            Mes <span className="text-blue-400">Projets</span>
          </h2>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {projects.map((project, index) => (
              <div 
                key={project.id}
                className="bg-white bg-opacity-10 backdrop-blur-lg rounded-xl overflow-hidden shadow-xl hover:shadow-2xl transition-all duration-300 transform hover:scale-105 hover:bg-opacity-15"
                style={{ 
                  transform: `translateY(${scrollPosition > 300 ? '0' : '50px'}) 
                               rotate(${mousePosition.x * (index - 1)}deg)`,
                  opacity: scrollPosition > 300 ? 1 : 0,
                  transition: 'transform 0.5s ease-out, opacity 0.5s ease-out'
                }}
              >
                <div className="h-48 bg-gradient-to-r from-blue-600 to-purple-600 flex items-center justify-center">
                  <Monitor size={64} color="white" strokeWidth={1} />
                </div>
                <div className="p-6">
                  <h3 className="text-xl font-bold text-white mb-2">{project.title}</h3>
                  <p className="text-gray-300 mb-4">{project.description}</p>
                  <div className="flex flex-wrap gap-2 mb-4">
                    {project.technologies.map((tech) => (
                      <span 
                        key={tech} 
                        className="px-3 py-1 bg-blue-900 bg-opacity-50 text-blue-200 text-sm rounded-full"
                      >
                        {tech}
                      </span>
                    ))}
                  </div>
                  <a 
                    href="#" 
                    className="inline-block px-4 py-2 border border-blue-400 text-blue-400 rounded-lg hover:bg-blue-400 hover:text-white transition-colors duration-300"
                  >
                    Voir détails
                  </a>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Section compétences et outils - avec cercle de compétences */}
