        <div className="container mx-auto max-w-6xl">
          <h2 className="text-4xl font-bold text-white mb-12 text-center">
            Mes <span className="text-blue-400">Compétences & Outils</span>
          </h2>

          {/* Cercle de compétences */}
          <div className="flex flex-col items-center mb-16">
            <h3 className="text-2xl font-semibold text-white mb-8">Compétences techniques</h3>

            <div 
              className="relative h-80 w-full max-w-lg"
              style={{ 
                opacity: scrollPosition > 800 ? 1 : 0,
                transition: 'opacity 1s ease-out'
              }}
            >
              {/* Cercle central */}
              <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-24 h-24 rounded-full bg-blue-500 bg-opacity-20 backdrop-blur-md flex items-center justify-center z-0 pulsating">
                <span className="text-white text-lg font-semibold">Tech</span>
              </div>

              {/* Skills autour du cercle */}
              <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
                {generateSkillsCircle()}
              </div>
            </div>
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-6 text-center">Outils préférés</h3>

            <div 
              className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-6 gap-6"
              style={{ 
                transform: `translateY(${scrollPosition > 900 ? '0' : '50px'})`,
                opacity: scrollPosition > 900 ? 1 : 0,
                transition: 'transform 0.5s ease-out, opacity 0.5s ease-out'
              }}
            >
              {['VS Code', 'Git', 'Figma', 'npm', 'Jest', 'Webpack'].map((tool) => (
                <div 
                  key={tool}
                  className="bg-white bg-opacity-10 backdrop-blur-sm rounded-xl p-4 flex flex-col items-center justify-center text-center tool-card"
                >
                  <div className="w-16 h-16 mb-3 bg-gray-800 rounded-full flex items-center justify-center">
                    <Code size={32} color="white" />
                  </div>
                  <p className="text-white font-medium">{tool}</p>
                </div>
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Section contact */}
      <section id="contact" className="py-20 px-6">
        <div className="container mx-auto max-w-4xl">
          <h2 className="text-4xl font-bold text-white mb-12 text-center">
            Me <span className="text-blue-400">Contacter</span>
          </h2>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
            <div>
              <h3 className="text-2xl font-semibold text-white mb-6">Envoyez-moi un message</h3>

              <form className="space-y-4">
                <div>
                  <label htmlFor="name" className="block text-white mb-2">Nom complet</label>
                  <input 
                    type="text" 
                    id="name" 
                    className="w-full px-4 py-3 bg-white bg-opacity-10 border border-gray-600 rounded-lg focus:outline-none focus:border-blue-400 text-white"
                    placeholder="Votre nom"
                  />
                </div>

                <div>
                  <label htmlFor="email" className="block text-white mb-2">Email</label>
                  <input 
                    type="email" 
                    id="email" 
                    className="w-full px-4 py-3 bg-white bg-opacity-10 border border-gray-600 rounded-lg focus:outline-none focus:border-blue-400 text-white"
                    placeholder="votre@email.com"
                  />
                </div>

                <div>
                  <label htmlFor="message" className="block text-white mb-2">Message</label>
                  <textarea 
                    id="message" 
                    rows="5" 
                    className="w-full px-4 py-3 bg-white bg-opacity-10 border border-gray-600 rounded-lg focus:outline-none focus:border-blue-400 text-white resize-none"
                    placeholder="Votre message..."
                  ></textarea>
                </div>

                <button 
                  type="submit" 
                  className="w-full py-3 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-lg transition-colors duration-300"
                >
                  Envoyer
                </button>
              </form>
            </div>

            <div className="flex flex-col justify-center">
              <h3 className="text-2xl font-semibold text-white mb-6">Coordonnées</h3>

              <div className="space-y-6">
                <div className="flex items-center">
                  <div className="w-12 h-12 bg-blue-600 bg-opacity-20 rounded-full flex items-center justify-center mr-4">
                    <Mail size={24} color="white" />
                  </div>
                  <div>
                    <p className="text-gray-400">Email</p>
                    <p className="text-white font-medium">contact@devportfolio.com</p>
                  </div>
                </div>

                <div className="flex items-center">
                  <div className="w-12 h-12 bg-blue-600 bg-opacity-20 rounded-full flex items-center justify-center mr-4">
                    <User size={24} color="white" />
                  </div>
                  <div>
                    <p className="text-gray-400">Basé à</p>
                    <p className="text-white font-medium">Paris, France</p>
                  </div>
                </div>

                <div>
                  <p className="text-white mb-4">Retrouvez-moi sur</p>
                  <div className="flex space-x-4">
                    <a href="#" className="w-12 h-12 bg-white bg-opacity-10 rounded-full flex items-center justify-center social-icon">
                      <Github size={24} color="white" />
                    </a>
                    <a href="#" className="w-12 h-12 bg-white bg-opacity-10 rounded-full flex items-center justify-center social-icon">
                      <Linkedin size={24} color="white" />
                    </a>
                    <a href="#" className="w-12 h-12 bg-white bg-opacity-10 rounded-full flex items-center justify-center social-icon">
                      <Twitter size={24} color="white" />
                    </a>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="py-8 bg-black bg-opacity-70">
        <div className="container mx-auto text-center">
          <p className="text-gray-400">
            © {new Date().getFullYear()} DevPortfolio. Tous droits réservés.
          </p>
        </div>
