import React, { useRef, useState, useEffect, Suspense } from 'react';
import * as THREE from 'three';
import { Canvas, useFrame, useThree } from '@react-three/fiber';
import { Html, OrbitControls } from '@react-three/drei';
import './Portfolio.css'; // Assurez-vous de créer ce fichier CSS

// Données des compétences
const skills = [
  { 
    name: "HTML5", 
    category: "front-end",
    icon: "https://cdn.worldvectorlogo.com/logos/html-1.svg"
  },
  { 
    name: "CSS3", 
    category: "front-end",
    icon: "https://cdn.worldvectorlogo.com/logos/css-3.svg"
  },
  { 
    name: "JavaScript", 
    category: "front-end",
    icon: "https://cdn.worldvectorlogo.com/logos/javascript-1.svg"
  },
  { 
    name: "Bootstrap", 
    category: "front-end",
    icon: "https://cdn.worldvectorlogo.com/logos/bootstrap-5-1.svg"
  },
  { 
    name: "React", 
    category: "front-end",
    icon: "https://cdn.worldvectorlogo.com/logos/react-2.svg"
  },
  { 
    name: "Angular", 
    category: "front-end",
    icon: "https://cdn.worldvectorlogo.com/logos/angular-icon-1.svg"
  },
  { 
    name: "Vue.js", 
    category: "front-end",
    icon: "https://cdn.worldvectorlogo.com/logos/vue-9.svg"
  },
  { 
    name: "Tailwind CSS", 
    category: "front-end",
    icon: "https://cdn.worldvectorlogo.com/logos/tailwind-css-2.svg"
  },
  { 
    name: "TypeScript", 
    category: "front-end",
    icon: "https://cdn.worldvectorlogo.com/logos/typescript.svg"
  },
  { 
    name: "Node.js", 
    category: "back-end",
    icon: "https://cdn.worldvectorlogo.com/logos/nodejs-icon.svg"
  },
  { 
    name: "Python", 
    category: "back-end",
    icon: "https://cdn.worldvectorlogo.com/logos/python-5.svg"
  },
  { 
    name: "Java", 
    category: "back-end",
    icon: "https://cdn.worldvectorlogo.com/logos/java-14.svg"
  },
  { 
    name: "SpringBoot", 
    category: "back-end", 
    icon: "https://spring.io/img/spring-2.svg" 
  },
  { 
    name: "Symfony", 
    category: "back-end", 
    icon: "https://cdn.worldvectorlogo.com/logos/symfony.svg" 
  },
  { 
    name: "Laravel", 
    category: "back-end", 
    icon: "https://cdn.worldvectorlogo.com/logos/laravel-2.svg" 
  },
  { 
    name: "C", 
    category: "back-end", 
    icon: "https://cdn.worldvectorlogo.com/logos/c-1.svg" 
  },
  { 
    name: "C++", 
    category: "back-end", 
    icon: "https://cdn.worldvectorlogo.com/logos/c.svg" 
  },
  { 
    name: "C#", 
    category: "back-end", 
    icon: "https://cdn.worldvectorlogo.com/logos/c--4.svg" 
  },
  { 
    name: "MySQL", 
    category: "database", 
    icon: "https://cdn.worldvectorlogo.com/logos/mysql-3.svg" 
  },
  { 
    name: "MongoDB", 
    category: "database", 
    icon: "https://cdn.worldvectorlogo.com/logos/mongodb-icon-1.svg" 
  },

  { 
    name: "Oracle", 
    category: "database", 
    icon: "https://cdn.worldvectorlogo.com/logos/oracle-6.svg" 
  },
  { 
    name: "SQL Server", 
    category: "database", 
    icon: "https://cdn.worldvectorlogo.com/logos/microsoft-sql-server-1.svg" 
  },

  { 
    name: "WordPress", 
    category: "cms/ecommerce", 
    icon: "https://cdn.worldvectorlogo.com/logos/wordpress-icon-1.svg" 
  },
  { 
    name: "Shopify", 
    category: "cms/ecommerce", 
    icon: "https://cdn.worldvectorlogo.com/logos/shopify.svg" 
  },
  { 
    name: "GitLab CI/CD", 
    category: "devops", 
    icon: "https://cdn.worldvectorlogo.com/logos/gitlab.svg" 
  },
  { 
    name: "Github", 
    category: "devops", 
    icon: "https://cdn.worldvectorlogo.com/logos/github-icon-1.svg" 
  },
  { 
    name: "Git", 
    category: "devops", 
    icon: "https://cdn.worldvectorlogo.com/logos/git.svg" 
  },
  { 
    name: "Docker", 
    category: "devops", 
    icon: "https://cdn.worldvectorlogo.com/logos/docker.svg" 
  },

];

// Composant pour les étoiles de niveau
const SkillStars = ({ level }) => {
  return (
    <div className="skill-stars">
      {Array.from({ length: 5 }).map((_, i) => (
        <span key={i} className={i < level ? 'star filled' : 'star'}>★</span>
      ))}
    </div>
  );
};

// Composant pour chaque logo de compétence
// Composant pour chaque logo de compétence
const SkillLogo = ({ skill, position, index }) => {
  const meshRef = useRef();
  const [hovered, setHover] = useState(false);
  const [texture, setTexture] = useState(null);
  const [loaded, setLoaded] = useState(false);
  const [error, setError] = useState(false);

  // Charger l'image SVG comme texture
  useEffect(() => {
    const textureLoader = new THREE.TextureLoader();

    // Essayer de charger l'image
    textureLoader.load(
      skill.icon,
      (loadedTexture) => {
        setTexture(loadedTexture);
        setLoaded(true);
      },
      undefined,
      (err) => {
        console.error(`Erreur de chargement pour ${skill.name}:`, err);
        setError(true);
      }
    );
  }, [skill.icon, skill.name]);

  // Animation de perturbation
  useFrame((state) => {
    if (!meshRef.current) return;

    if (!hovered) {
      // Mouvement perturbé non organisé
      meshRef.current.position.x = position[0] + Math.sin(state.clock.elapsedTime * (0.3 + index * 0.05) + index) * 0.3;
      meshRef.current.position.y = position[1] + Math.cos(state.clock.elapsedTime * (0.2 + index * 0.03) + index * 2) * 0.3;
      meshRef.current.rotation.z = Math.sin(state.clock.elapsedTime * (0.1 + index * 0.02)) * 0.2;
    } else {
      // Position stable en cas de hover
      meshRef.current.position.x = position[0];
      meshRef.current.position.y = position[1];
      meshRef.current.rotation.z = 0;
    }
  });

  // Définir une couleur par défaut basée sur la catégorie de compétence
  const getColorByCategory = (category) => {
    switch(category) {
      case 'front-end': return '#61DAFB'; // Bleu React
      case 'back-end': return '#3C873A'; // Vert Node.js
      case 'database': return '#F29111'; // Orange MySQL
      case 'cms/ecommerce': return '#21759B'; // Bleu WordPress
      case 'devops': return '#F05033'; // Rouge Git
      default: return '#6C63FF'; // Violet par défaut
    }
  };

  // Couleur basée sur la catégorie
  const color = getColorByCategory(skill.category);

  return (
    <mesh
      ref={meshRef}
      position={[position[0], position[1], 0]}
      onPointerOver={() => setHover(true)}
      onPointerOut={() => setHover(false)}
      scale={hovered ? 1.2 : 1}
    >
      {/* Plan pour afficher la texture */}
      <planeGeometry args={[1.5, 1.5]} />

      {/* Si la texture est chargée, l'utiliser, sinon utiliser une couleur */}
      {loaded && texture ? (
        <meshBasicMaterial 
          map={texture} 
          transparent 
          opacity={hovered ? 1 : 0.8}
        />
      ) : (
        <meshPhongMaterial 
          color={color} 
          transparent 
          opacity={hovered ? 1 : 0.8}
          emissive={color}
          emissiveIntensity={hovered ? 0.5 : 0}
          wireframe={index % 3 === 0} // Variation: certaines compétences en wireframe
        />
      )}

      {/* Afficher le nom de la compétence et un niveau d'expertise (ajouté pour l'exemple) */}
      {hovered && (
        <Html center position={[0, -1.2, 0]}>
          <div className="skill-tooltip">
            <p className="skill-name">{skill.name}</p>
            <SkillStars level={3} /> {/* Valeur par défaut, vous pouvez ajouter un niveau à chaque compétence */}
          </div>
        </Html>
      )}
    </mesh>
  );
};

// Composant pour la scène des compétences
const SkillsScene = () => {
  const { viewport } = useThree();
  const [positions, setPositions] = useState([]);

  // Calculer des positions dispersées pour les logos
  useEffect(() => {
    const newPositions = [];
    const spacing = Math.min(viewport.width, viewport.height) / 4;
    const area = Math.max(skills.length * 2, 12); // S'assurer d'avoir assez d'espace

    // Distribution non régulière (effet perturbé)
    for (let i = 0; i < skills.length; i++) {
      let x, y;
      let overlapping;
      let attempts = 0;

      // Éviter les chevauchements
      do {
        overlapping = false;
        x = (Math.random() - 0.5) * viewport.width * 0.7;
        y = (Math.random() - 0.5) * viewport.height * 0.7;

        // Vérifier la distance avec les positions existantes
        for (let j = 0; j < newPositions.length; j++) {
          const dx = newPositions[j][0] - x;
          const dy = newPositions[j][1] - y;
          const distance = Math.sqrt(dx * dx + dy * dy);

          if (distance < 1.5) {
            overlapping = true;
            break;
          }
        }

        attempts++;
      } while (overlapping && attempts < 100); // Limite les tentatives

      newPositions.push([x, y]);
    }

    setPositions(newPositions);
  }, [viewport, skills.length]);

  return (
    <>
      <ambientLight intensity={0.3} />
      <pointLight position={[10, 10, 10]} intensity={1} />
      <pointLight position={[-10, -10, -10]} intensity={0.5} />

      {skills.map((skill, index) => (
        positions[index] && 
        <SkillLogo 
          key={skill.name} 
          skill={skill} 
          position={positions[index]} 
          index={index} 
        />
      ))}
    </>
  );
};

// Animation pour la section Hero
const HeroMesh = () => {
  const mesh = useRef();

  useFrame((state) => {
    if (mesh.current) {
      mesh.current.rotation.x = Math.sin(state.clock.elapsedTime * 0.3) * 0.2;
      mesh.current.rotation.y += 0.01;
      mesh.current.rotation.z = Math.sin(state.clock.elapsedTime * 0.2) * 0.1;
    }
  });

  return (
    <mesh ref={mesh}>
      <torusKnotGeometry args={[1.5, 0.5, 128, 32]} />
      <meshStandardMaterial 
        color="#8A2BE2"  // Violet
        emissive="#4B0082"  // Indigo
        emissiveIntensity={0.5}
        wireframe
      />
    </mesh>
  );
};

// Section Compétences
const Skills = () => {
  return (
    <section id="skills" className="section skills-section">
      <div className="container">
        <h2 className="section-title">Compétences techniques</h2>
        <div className="skills-canvas-container">
          <Canvas camera={{ position: [0, 0, 10], fov: 50 }}>
            <Suspense fallback={null}>
              <SkillsScene />
              <OrbitControls enableZoom={false} enablePan={false} />
            </Suspense>
          </Canvas>
        </div>
      </div>
    </section>
  );
};

// Section Hero
const Hero = () => {
  return (
    <section id="hero" className="section hero-section">
      <Canvas className="hero-canvas" camera={{ position: [0, 0, 5] }}>
        <Suspense fallback={null}>
          <ambientLight intensity={0.2} />
          <pointLight position={[10, 10, 10]} />
          <HeroMesh />
          <OrbitControls enableZoom={false} enablePan={false} />
        </Suspense>
      </Canvas>
      <div className="hero-content">
        <h1>MOUAD SALAH</h1>
        <p className="hero-subtitle">Développeur Web | Créatif | Innovant</p>
        <div className="hero-cta">
          <a href="#contact" className="btn primary-btn">Me contacter</a>
          <a href="#projects" className="btn secondary-btn">Voir mes projets</a>
        </div>
      </div>
    </section>
  );
};

// Section Projets (exemple)
const Projects = () => {
  return (
    <section id="projects" className="section projects-section">
      <div className="container">
        <h2 className="section-title">Mes Projets</h2>
        <div className="projects-grid">
          {/* Projets à ajouter ici */}
          <div className="project-card">
            <div className="project-content">
              <h3>Projet 1</h3>
              <p>Description du projet...</p>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

// Section Contact (exemple)
const Contact = () => {
  return (
    <section id="contact" className="section contact-section">
      <div className="container">
        <h2 className="section-title">Contact</h2>
        <form className="contact-form">
          <div className="form-group">
            <label htmlFor="name">Nom</label>
            <input type="text" id="name" name="name" required />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input type="email" id="email" name="email" required />
          </div>
          <div className="form-group">
            <label htmlFor="message">Message</label>
            <textarea id="message" name="message" rows="5" required></textarea>
          </div>
          <button type="submit" className="btn primary-btn">Envoyer</button>
        </form>
      </div>
    </section>
  );
};

// Composant principal du Portfolio
const Portfolio = () => {
  return (
    <div className="portfolio">
      <header>
        <nav className="navbar">
          <div className="logo">MS</div>
          <ul className="nav-links">
            <li><a href="#hero">Accueil</a></li>
            <li><a href="#skills">Compétences</a></li>
            <li><a href="#projects">Projets</a></li>
            <li><a href="#contact">Contact</a></li>
          </ul>
        </nav>
      </header>

      <main>
        <Hero />
        <Skills />
        <Projects />
        <Contact />
      </main>

      <footer>
        <div className="container">
          <p>&copy; {new Date().getFullYear()} Votre Nom. Tous droits réservés.</p>
          <div className="social-links">
            <a href="https://github.com/" target="_blank" rel="noopener noreferrer">GitHub</a>
            <a href="https://linkedin.com/" target="_blank" rel="noopener noreferrer">LinkedIn</a>
            <a href="https://twitter.com/" target="_blank" rel="noopener noreferrer">Twitter</a>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default Portfolio;
